text:
Code
%% ojs
//| echo: false
// 定义题目
viewof q1 = Inputs.radio(
    ["追求极致性能 (C++/Rust)", "追求开发效率 (Python/JS)", "追求系统稳健 (Java/Go)"],
    { label: "1. 你最看重的开发维度是什么？", value: "追求开发效率 (Python/JS)" }
)

viewof q2 = Inputs.range([0, 10], { label: "2. 你对新技术的“尝鲜”欲望 (0-10)：", step: 1 })




Code
%% ojs
//| echo: false
// 响应式计算结果
results = {
    let score = 0;
    if(q1.includes("性能")) score += 40;
    if(q1.includes("效率")) score += 20;
    if(q1.includes("稳健")) score += 30;

    score += q2 * 6;
  return score;
}

// 根据分数匹配人格
profile = {
    if(results &gt; 80) return { title: "硬核极客", desc: "你喜欢钻研底层，对代码有着像素级的掌控欲。" };
if (results &gt; 50) return { title: "务实开发者", desc: "你善于利用工具解决问题，平衡艺术与现实。" };
return { title: "创意工程师", desc: "你更关注产品本身，技术是你实现创意的画笔。" };
}




Code
%% ojs
//| echo: false
md`
---
### 🎊 测试结果：${profile.title}
&gt; ${profile.desc}

**你的技术倾向性指数：${results} / 100**
`
